document.addEventListener("alpine:init",()=>{Alpine.data("progressBar",()=>({progress:0,contentFull:null,init(){if(this.contentFull=document.querySelector(".content-full"),!this.contentFull)return;document.querySelectorAll(".progress-bar").forEach(s=>s.remove()),this.updateProgress();let t=!1;window.addEventListener("scroll",()=>{t||(requestAnimationFrame(()=>{this.updateProgress(),t=!1}),t=!0)},{passive:!0}),window.addEventListener("resize",()=>this.updateProgress()),window.addEventListener("orientationchange",()=>this.updateProgress())},updateProgress(){if(!this.contentFull)return;const n=this.contentFull.getBoundingClientRect(),t=n.top+window.pageYOffset,s=n.height,o=window.innerHeight,i=window.pageYOffset;let e=0;if(i>t){const r=i-t,l=s-o;e=r/l*100,e=Math.min(Math.max(e,0),100)}this.progress=e}}))});
